mapper.insert:
INSERT INTO board_notice
(views, writer_id, date_of_created, title, content)
VALUES(0, #{writerId}, #{dateOfCreated}, #{title}, #{content});

mapper.update:
UPDATE board_notice
SET title=#{title}, content=#{content}, date_of_updated=#{dateOfUpdate}, ip_of_updated=#{ipOfUpdate}, member_id_of_updated=#{memberIdOfUpdated}
WHERE notice_id=#{noticeId};
