mapper.select:
SELECT	password
FROM	plaredb.member
WHERE	auth_id=#{authId};

mapper.insert:
INSERT INTO plaredb.member (
	auth_id,		password,			name,
	email,			date_of_join,		date_of_last_join,
	steamid
) VALUES (
	#{authId},		#{password},		#{name},
	#{email},		UNIX_TIMESTAMP(),	UNIX_TIMESTAMP(),
	#{steamid}
);

mapper.select:
SELECT	count(member_id)
FROM	plaredb.member
WHERE	auth_id=#{authId};
