mapper.select:
SELECT	password
FROM	plaredb.member
WHERE	auth_id=#{auth_id};

mapper.insert:
INSERT INTO plaredb.member (
	auth_id,		password,			name,
	email,			date_of_join,		date_of_last_join,
	steamid
) VALUES (
	#{auth_id},		#{password},		#{name},
	#{email},		UNIX_TIMESTAMP(),	UNIX_TIMESTAMP(),
	#{steamid}
);
